import fs from "node:fs";
import path from "node:path";
import { exec } from "node:child_process";
import { Type } from "@sinclair/typebox";

function loadTemplate(config: { command?: string }) {
  if (config?.command) return config.command;
  const toolsPath = path.resolve(
    process.env.OPENCLAW_WORKSPACE ?? process.cwd(),
    "tools",
    "ppt_tool.json",
  );
  try {
    const raw = fs.readFileSync(toolsPath, "utf8");
    const data = JSON.parse(raw);
    if (typeof data?.command === "string") return data.command;
  } catch {
    // Ignore and fall through.
  }
  return undefined;
}

function escapeForShell(value: string) {
  return value.replace(/"/g, "\\\"");
}

export default function register(api: any) {
  api.registerTool({
    name: "create_powerpoint",
    description:
      "Generate a PowerPoint using a local command template (expects {{title}} and {{content}} placeholders).",
    parameters: Type.Object({
      title: Type.String({ description: "PPT title" }),
      content: Type.String({ description: "PPT homepage content" }),
    }),
    async execute(_id: string, params: { title: string; content: string }) {
      const template = loadTemplate(api.config?.plugins?.entries?.["ppt-tool"]?.config ?? {});
      if (!template) {
        return {
          content: [
            {
              type: "text",
              text:
                "create_powerpoint: Êú™ÊâæÂà∞ÂèØÁî®ÁöÑ command Ê®°ÊùøÔºåËØ∑Âú® plugins.entries.ppt-tool.config.command Êàñ tools/ppt_tool.json ‰∏≠ÈÖçÁΩÆ„ÄÇ",
            },
          ],
        };
      }

      const title = escapeForShell(params.title);
      const content = escapeForShell(params.content);
      const command = template
        .replace(/\{\{title\}\}/g, title)
        .replace(/\{\{content\}\}/g, content);

      const cwd = api.config?.plugins?.entries?.["ppt-tool"]?.config?.cwd;

      const result = await new Promise<{ stdout: string; stderr: string }>((resolve) => {
        exec(command, { cwd, windowsHide: true }, (error, stdout, stderr) => {
          if (error) {
            const message = `${stderr || error.message}`.trim();
            resolve({ stdout: "", stderr: message || "Unknown error" });
            return;
          }
          resolve({ stdout: stdout.trim(), stderr: stderr.trim() });
        });
      });

      const lines = [
        "create_powerpoint: ÂëΩ‰ª§Â∑≤ÊâßË°å",
        result.stdout && `stdout: ${result.stdout}`,
        result.stderr && `stderr: ${result.stderr}`,
      ].filter(Boolean);

      return { content: [{ type: "text", text: lines.join("\n") }] };
    },
  });
}

