const fs = require('fs');
const path = require('path');
const lamejs = require('/tmp/mp3enc/node_modules/lamejs');

const wavPath = '/home/ubuntu/.openclaw/workspace/adventure_8bit_10s.wav';
const mp3Path = '/home/ubuntu/.openclaw/workspace/adventure_8bit_10s.mp3';
const wav = fs.readFileSync(wavPath);

function readUInt32LE(buf, off){return buf.readUInt32LE(off);} 
function readUInt16LE(buf, off){return buf.readUInt16LE(off);} 

// Simple WAV parser (PCM)
if (wav.toString('ascii',0,4) !== 'RIFF' || wav.toString('ascii',8,12) !== 'WAVE') {
  throw new Error('Invalid WAV');
}
let offset = 12;
let fmt, data;
while (offset < wav.length) {
  const id = wav.toString('ascii', offset, offset+4);
  const size = readUInt32LE(wav, offset+4);
  const start = offset + 8;
  if (id === 'fmt ') fmt = wav.slice(start, start+size);
  if (id === 'data') { data = wav.slice(start, start+size); break; }
  offset = start + size + (size % 2);
}
if (!fmt || !data) throw new Error('Missing chunks');
const audioFormat = readUInt16LE(fmt, 0);
const numChannels = readUInt16LE(fmt, 2);
const sampleRate = readUInt32LE(fmt, 4);
const bitsPerSample = readUInt16LE(fmt, 14);
if (audioFormat !== 1 || bitsPerSample !== 16) throw new Error('Unsupported WAV format');

// Convert interleaved 16-bit PCM to Int16Array
const samples = new Int16Array(data.buffer, data.byteOffset, data.byteLength/2);
const mp3enc = new lamejs.Mp3Encoder(numChannels, sampleRate, 96);
let mp3Data = [];
const blockSize = 1152;
if (numChannels === 1) {
  for (let i=0; i<samples.length; i+=blockSize) {
    const chunk = samples.subarray(i, i+blockSize);
    const mp3buf = mp3enc.encodeBuffer(chunk);
    if (mp3buf.length > 0) mp3Data.push(Buffer.from(mp3buf));
  }
} else {
  const left = new Int16Array(samples.length/2);
  const right = new Int16Array(samples.length/2);
  for (let i=0, j=0; i<samples.length; i+=2, j++) { left[j]=samples[i]; right[j]=samples[i+1]; }
  for (let i=0; i<left.length; i+=blockSize) {
    const l = left.subarray(i, i+blockSize);
    const r = right.subarray(i, i+blockSize);
    const mp3buf = mp3enc.encodeBuffer(l, r);
    if (mp3buf.length > 0) mp3Data.push(Buffer.from(mp3buf));
  }
}
const end = mp3enc.flush();
if (end.length > 0) mp3Data.push(Buffer.from(end));
fs.writeFileSync(mp3Path, Buffer.concat(mp3Data));
console.log(mp3Path);
NODE

